def reference(keys, query):
    assert all(keys[i] < keys[i + 1] for i in range(len(keys) - 1))
    assert 1 <= len(keys) <= 10 ** 4

    left, right = 0, len(keys)
    while left + 1 < right:
        ave = (left + right) // 2
        if keys[ave] <= query:
            left = ave
        else:
            right = ave
    if keys[left] != query:
        return -1
    else:
        return left


