def reference(s, t):
    sn = len(s)
    tn = len(t)

    f = [[10**9] * (tn + 2) for _ in range(sn + 2)]
    f[0][0] = 0

    def relax(p, q, x):
        f[p][q] = min(f[p][q], x)

    for i in range(sn + 1):
        for j in range(tn + 1):
            if i < sn and j < tn:
                relax(i + 1, j + 1, f[i][j] + (1 if s[i] != t[j] else 0))
            relax(i + 1, j, f[i][j] + 1)
            relax(i, j + 1, f[i][j] + 1)
    return f[sn][tn]


