from lcs2 import lcs2


def reference(a, b):
    n, m = len(a), len(b)
    t = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if a[i - 1] == b[j - 1]:
                t[i][j] = max(t[i - 1][j - 1] + 1, t[i][j - 1], t[i - 1][j])
            else:
                t[i][j] = max(t[i - 1][j], t[i][j - 1])

    return t[n][m]


